//Test
func TestCanThreePartsEqualSum(t *testing.T) {
	for _, table := range tables {
		y := canThreePartsEqualSum(table.x)
		if y != table.y {
			var tmp []string
			for k := range table.x {
				tmp = append(tmp, strconv.Itoa(table.x[k]))
			}
			t.Fatalf("error" + strings.Join(tmp, ","))
		}
	}

}

